<?php

namespace Tests\Feature;

use Illuminate\Foundation\Testing\RefreshDatabase;
use Illuminate\Support\Facades\Http;
use Livewire\Livewire;
use Tests\TestCase;

class ViewMoviesTest extends TestCase
{

    /**
     *
     */
    protected function setUp(): void
    {
        parent::setUp();

        Http::fake([
            'https://api.themoviedb.org/3/movie/popular' => $this->fakePopularMovies(),
            'https://api.themoviedb.org/3/movie/now_playing' => $this->fakeNowPLayingMovies(),
            'https://api.themoviedb.org/3/genre/movie/list' => $this->fakeGenres(),
            'https://api.themoviedb.org/3/movie/*' => $this->fakeMovie(),
            'https://api.themoviedb.org/3/search/movie?query=*' => $this->fakeSearch(),
        ]);
    }

    /**
     * A basic test example.
     *
     * @return void
     */
    public function testMainPageShowsCorrectInfo()
    {
        $response = $this->get(route('movies.index'));

        $response->assertStatus(200);
        $response->assertSee('Movies - Popular');
        $response->assertSee('Test Fake Movie');
        $response->assertSee('Drama');
        $response->assertSee('Science Fiction');

        // not covering this line right now...
        // $html = $response->getContent();
        // $this->assertEquals(1, preg_match('/Drama, *Science Fiction/', $html), 'Page doenst contain expected genres');

        $response->assertSee('Movies - Now Playing');
        $response->assertSee('Test Fake Movie 2');
    }

    public function testMoviePageShowsCorrectInfo()
    {
        $response = $this->get(route('movies.show', 419704));

        $response->assertSee('Ad Astra (2019)');
        $response->assertSee('Featured Crew');
        $response->assertSee('Brad Pitt');
        $response->assertSee('Cast');
        $response->assertSee('Roy McBride');
        $response->assertSee('Tommy Lee Jones');
        $response->assertSee('Scenes');
    }

    public function testSearchDropdownWorks()
    {
        Livewire::test('search-dropdown')
            ->assertDontSee('Avengers')
            ->set('search', 'Avengers')
            ->assertSee('Avengers');
    }

